import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import * as emailjs from "emailjs-com";
import { Button, Form, FormGroup, Input } from "reactstrap";
import "./Contact.css";
import "../App/App.css";
import Nav from "../Nav/Nav";
import { UncontrolledAlert } from "reactstrap";
import Burger from "../Burger/Burger";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faLinkedin } from "@fortawesome/free-brands-svg-icons";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";

class Contact extends Component {
  state = {
    name: "",
    email: "",
    message: "",
    success: "",
  };
  handleSubmit(e) {
    e.preventDefault();
    var templateParams = {
      from_name: this.state.name + " (" + this.state.email + ")",
      to_name: "Ross",
      message_html: this.state.message,
    };
    emailjs
      .send(
        "gmail",
        "template_DMlIudKC",
        templateParams,
        "user_uXigZw2RYzgOCTjVxakmu"
      )
      .then(
        (result) => {
          if (result.text === "OK") {
            this.setState({
              success: (
                <UncontrolledAlert classname="alert" color="success">
                  Message sent!
                </UncontrolledAlert>
              ),
            });
            console.log("success");
          } else {
            this.setState({
              success: (
                <UncontrolledAlert className="alert" color="danger">
                  Uh oh, something went wrong!
                </UncontrolledAlert>
              ),
            });
            console.log("failure");
          }
        },
        (error) => {
          console.log(error.text);
        }
      );

    this.resetForm();
  }
  resetForm() {
    this.setState({
      name: "",
      email: "",
      message: "",
    });
  }
  handleChange = (param, e) => {
    this.setState({ [param]: e.target.value });
  };
  render() {
    return (
      <div className="App" id="contactID">
        <div id="body">
          <Burger />
          <Nav />
          <main id="main">
            <div className="form">
              <section id="contactSection">
                <h1 className="p-heading1">contact me</h1>
                <div className="contact-options-box">
                  <ul className="contact-options">
                    <li className="contact-option">
                      <a
                        href="https://www.linkedin.com/in/rsmacdonald/"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        <FontAwesomeIcon
                          className="contact-icon"
                          icon={faLinkedin}
                        />
                        LinkedIn
                      </a>
                    </li>
                    <li className="contact-option">
                      <a
                        href="https://www.github.com/rmac2289"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        <FontAwesomeIcon
                          className="contact-icon"
                          icon={faGithub}
                        />
                        GitHub
                      </a>
                    </li>
                    <li className="contact-option">
                      <a href="mailto:rmac2289@gmail.com">
                        <FontAwesomeIcon
                          className="contact-icon"
                          icon={faEnvelope}
                        />
                        Email
                      </a>
                    </li>
                  </ul>
                </div>
                <Form id="form" onSubmit={this.handleSubmit.bind(this)}>
                  <FormGroup className="formGroup" controlid="formBasicEmail">
                    <label htmlFor="email">email</label>
                    <Input
                      id="email"
                      type="email"
                      name="email"
                      value={this.state.email}
                      onChange={this.handleChange.bind(this, "email")}
                      className="text-primary"
                      placeholder="recruiter@successfulcompany.com"
                    />
                  </FormGroup>
                  <FormGroup className="formGroup" controlid="formBasicName">
                    <label htmlFor="name">name</label>
                    <Input
                      id="name"
                      type="text"
                      name="name"
                      value={this.state.name}
                      onChange={this.handleChange.bind(this, "name")}
                      className="text-primary"
                      placeholder="John Smith"
                    />
                  </FormGroup>
                  <FormGroup className="formGroup" controlid="formBasicMessage">
                    <label htmlFor="message">message</label>
                    <Input
                      id="message"
                      type="textarea"
                      name="message"
                      className="text-primary"
                      onChange={this.handleChange.bind(this, "message")}
                      value={this.state.message}
                      placeholder="Top-notch portfolio, I'd like to hire you immediately."
                    />
                  </FormGroup>
                  {this.state.success}
                  <Button id="button" variant="primary" type="submit">
                    send message
                  </Button>
                </Form>
              </section>
            </div>
          </main>
        </div>
      </div>
    );
  }
}

export default Contact;
